        }

        filePath = filename;
        if (filename[0] == '~') {
            filePath.replace(0, 1, homePath);
        }
        return true;
    }


    static bool createDirectory(const std::string &amp;path_str) {
        struct stat info{};

        auto path = path_str.c_str();
        if (stat(path, &amp;info) != 0) {  // ​检查​文件夹​是否​已经​存在​
            if (mkdir(path, S_IRWXU | S_IRWXG | S_IROTH | S_IXOTH) == 0) {
