    std::map&lt;int, std::string&gt; m;
    m.emplace(1, "111");
    m.emplace(2, "222");

    StringBuffer sb;
    Writer&lt;StringBuffer&gt; writer(sb);

    writer.StartObject();
    writer.Key("m");
    serializeMaps(m, &amp;writer);
    writer.EndObject();

    puts(sb.GetString());
}
